import React, { useState } from 'react';
import Header from './components/header';
import SearchBar from './components/searchBar';
import LostItemsList from './components/lostItemsList';
import ReportLostItemForm from './components/ReportLostItemForm';

function App() {
  const [lostItems, setLostItems] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (query) => {
    const results = lostItems.filter(item => 
      item.name.includes(query) || item.idNumber.includes(query)
    );
    setSearchResults(results);
  };

  const handleReport = (newItem) => {
    setLostItems([...lostItems, newItem]);
  };

  return (
    <div>
      <Header />
      <SearchBar onSearch={handleSearch} />
      <LostItemsList items={searchResults.length ? searchResults : lostItems} />
      <ReportLostItemForm onSubmit={handleReport} />
    </div>
  );
}

export default App;
